	protected function render() {
		$settings = $this->get_settings_for_display();
		$image    = ! empty( $settings['image']['url'] ) ? wp_get_attachment_image_url( $settings['image']['id'], 'full' ) : '';
		$args = array(
			'post_type'   => 'courses',
			'p' => intval($settings['selected_course']),
		);
		$course = get_posts($args);
		$selected_course = count($course) ? $course[0] : [];
		$this->get_more_course_info($selected_course);
		//var_dump($selected_course);
		$course_professors = [];
		foreach($selected_course->course_professors as $val) {
			//professor_page_link, professor_image, professor_first_name, professor_last_name, professor_title
			//var_dump($val);
			$course_professors[] = $this->get_professor_info($val);
			//var_dump($this_faculty);
		}
		
		$breadHtml = auto_breadcrump_generation();
		//var_dump($course_professors);
		
		?>

		<div class="course-page-wrapper">
			<div class="course-header-wrapper">
				<div class="container">
					<div class="course-title-wrapper">
						<div class="course-description-title">Course Descriptions</div>
						<h1 class="course-name"><?php echo '<span class="name">' . $selected_course->course_name . '</span> <span class="code">LAW-'. $selected_course->course_number . '</span>'; ?></h1>
					</div>
					<?php if( ! empty ($breadHtml) ) { ?>
					<div class="auto-breadcrumb-wrap">
						<?php echo $breadHtml; ?>
					</div>
					<?php } ?>
				</div>
			</div>
			<div class="course-content-wrapper">
				<div class="container">
					<div class="course-description-next-step-wrapper">
						<div class="course-description">
							<?php echo $selected_course->course_description; ?>
						</div>
						<div class="course-next-step-wrapper d-none d-sm-block">
							<?php echo next_steps_sidebar_course_description(); ?>
						</div>
					</div>				
				</div>
			</div>
			<div class="course-more-wrapper">
				<div class="container">
					<div class="course-professor-wrapper">
						<div class="professor-title">
							<span>Professors Teaching This Course</span>
						</div>
						<div class="professor-list row">
							<?php foreach($course_professors as $key => $course_professor) { ?>
								<div class="col-sm-6 col-md-4 col-lg-3">
									<div class="profesor-wrapper">
										<div class="professor-thumb">
											<img src="<?php echo $course_professor->professor_image; ?>" alt="" />
										</div>
										<div class="professor-profile-wrapper">
											<div class="professor-profile-name">
												<?php echo $course_professor->professor_first_name . ' ' . $course_professor->professor_last_name; ?>
											</div>
											<div class="professor-profile-title">
												<?php echo $course_professor->professor_title; ?>
											</div>
											<div class="professor-profile-view">
												<a href="<?php echo $course_professor->professor_page_link; ?>">View Profile <img src="/wp-content/uploads/2023/03/icon-right-arrow.png" /></a>
											</div>
										</div>
									</div>
								</div>
							<?php } ?>
						</div>
					</div>
					<div class="course-options-wrapper">
						<div class="course-option-title">
							<span>Course Details</span>
						</div>
						<div class="course-options">
							<div class="course-option units">
								<span class="course-option-label">Units</span>
								<span class="course-option-value"><?php echo $selected_course->course_units; ?></span>
